# 6. Вывести символы в верхнем регистре, которые окружают ровно два символа в нижнем регистре слева и два символа в верхнем регистре справа. Решить задачу двумя способами: с помощью re и без.

import re

line = ""

# Без re

if (line[2].isupper() and line[3:5].isupper() and line[5].islower() and line[0:2].islower()):
        print(line[2])

for i in range(3, len(line) - 3):
        if (line[i].isupper() and line[i + 1:i + 3].isupper() and line[i + 3].islower()
                and line[i - 2:i].islower() and line[i - 3].isupper()):
                print(line[i])

if (line[-3].isupper() and line[-2:].isupper()
        and line[-5:-3].islower() and line[-6].isupper()):
        print(line[-3])

# С re

pattern = re.compile(r'([^a-z]|^)[a-z]{2}([A-Z])[A-Z]{2}([^A-Z]|$)')

matches = pattern.finditer(line)

for match in matches:
        print(match.group(2))
